<!-- control /join and /signin -->
<script>
  document.addeventlistener('domcontentloaded', function () {
    // find all anchor links that go to './join' or './signin'
    const joinlinks = document.queryselectorall(
      'a[href=\"./join\"], a[href=\"/join\"], a[href=\"./signin\"], a[href=\"/signin\"]'
    );

    joinlinks.foreach((link) => {
      link.addeventlistener('click', function (e) {
        e.preventdefault(); // stop default link behavior

        const currenthost = window.location.hostname; // e.g., newsela.com or staging.newseladev.com

        // extract subdomain and domain
        const parts = currenthost.split('.');
        let targethost;

        if (
          currenthost === 'newsela.com' ||
          currenthost.endswith('.newsela.com') ||
          currenthost === 'webflow.staging.newseladev.com' ||
          currenthost.endswith('webflow.staging.newseladev.com')
        ) {
          targethost = 'app.newsela.com';
        } else {
          // for dev.newseladev.com, we keep the same host
          if (currenthost === 'dev.newseladev.com' ||
            currenthost.endswith('.dev.newseladev.com')) {
            targethost = currenthost;
          } else {
            // for other environments like staging.newseladev.com
            targethost = 'app.' + currenthost;
          }
        }

        // determine the target path based on the original link
        const originalhref = link.getattribute('href');
        const targetpath = originalhref.includes('signin') ? '/signin' : '/join';

        const targeturl = `https://${targethost}${targetpath}`;
        window.location.href = targeturl;
      });
    });
  });
</script>
<!-- end control /join and /signin -->

