<script>
// add event listeners to the cookie banner buttons
document.addeventlistener('domcontentloaded', () => {
  const buttonclassnames = [
    'cookie-policy-accept',
    'cookie-policy-denny',
    \"save-preference-btn-handler\"
  ];

  // function to handle click events
  const trackevent = (action) => {
	console.log('in track event')
    if (window.datalayer) {
      // send event to google tag manager
      window.datalayer.push({
        event: 'cookie_banner_interaction',
        action: action, // button id or description
        timestamp: new date().toisostring(),
      });

	//debugger log, delete on prod
      console.log({
        event: 'cookie_banner_interaction',
        action: action, // button id or description
        timestamp: new date().toisostring(),
      });
    }else{
    	console.log('error')
    }
  };

  // attach click event listeners
  buttonclassnames.foreach((element) => {
    const button = document.getelementsbyclassname(element)[0];
    if (button) {
      button.addeventlistener('click', () => trackevent(element));
    }else{
    	console.log('there is no button')
    }
  });
});
</script>
