<script async=\"\">
  /*
    check if the user has consented to the necessary groups
    cookie types:
    1. necessary
    2. performance
    3. functional
    4. targeting
  */
  function hasgroupconsent(cookievalue) {
    const groups = new urlsearchparams(cookievalue).get('groups');
    const grouparray = groups?.split(',') || [];
    const hasc0001 = grouparray.some(group => group === 'c0001:1');
    const hasc0003 = grouparray.some(group => group === 'c0003:1');
    return (hasc0001 && hasc0003) || false;
  }

  // set a session cookie on correct domain
  function setsessioncookie(name, value) {
    if (window.location.hostname === 'newsela.webflow.io') {
      document.cookie = `${name}=${value}; path=/; domain=.newsela.webflow.io;`;
    }
    // remove comments to enable this on production
    /* else {
      document.cookie = `${name}=${value}; path=/; domain=.newsela.com;`;
    } */
  }

  // get a cookie by its name
  function getcookie(name) {
    const cookies = decodeuricomponent(document.cookie).split(';');
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startswith(name + \"=\")) {
        return cookie.substring(name.length + 1);
      }
    }
    return null;
  }

  // capture and store utm parameters only if consent is given
  function captureutmparameters() {
    const urlparams = new urlsearchparams(window.location.search);
    const utmparameters = ['utm_source', 'utm_medium', 'utm_campaign', 'utm_content', 'utm_term'];
    const consent = getcookie('optanonconsent');

    if (consent && hasgroupconsent(consent)) {
      utmparameters.foreach((utm) => {
        const paramvalue = urlparams.get(utm);
        if (paramvalue) {
          const existingvalue = getcookie(utm);
          if (!existingvalue || existingvalue !== paramvalue) {
            setsessioncookie(utm, paramvalue);
          }
        }
      });
    }
  }

  // execute utm capture on domcontentloaded
  document.addeventlistener('domcontentloaded', () => {
    captureutmparameters();
  });
</script>
